/**
 * ScandiPWA - Progressive Web App for Magento
 *
 * Copyright © Scandiweb, Inc. All rights reserved.
 * See LICENSE for license details.
 *
 * @license OSL-3.0 (Open Software License ("OSL") v. 3.0)
 * @package scandipwa/base-theme
 * @link https://github.com/scandipwa/base-theme
 */
import PropTypes from 'prop-types';
import { createRef, PureComponent } from 'react';
import { ChildrenType } from 'Type/Common';
import CSS from 'Util/CSS';

import './StepProgressBar.style';

/** @namespace Component/StepProgressBar/Component */
export class StepProgressBar extends PureComponent {
    static propTypes = {
        // children: ChildrenType.isRequired,
        // showArrow: PropTypes.bool,
        // showedItemCount: PropTypes.number,
        // showedActiveItemNr: PropTypes.number,
        // onChange: PropTypes.func,
        // activeItemId: PropTypes.number
    };

    static defaultProps = {
        // showArrow: true,
        // showedItemCount: 1,
        // showedActiveItemNr: 2,
        // onChange: () => {},
        // activeItemId: null
    };

    // state = {
    //     activeItemId: 0
    // };

    // itemRef = createRef();

    // carouselRef = createRef();

    // renderContent() {
    //     const { children } = this.props;

    //     return (
    //         <div block="CarouselScroll" elem="ContentWrapper">
    //             <div block="CarouselScroll" elem="Content">
    //                 { children.map(this.renderContentItem) }
    //             </div>
    //         </div>
    //     );
    // }


    renderProgressBar() {
        const {
            checkoutStep,
            stepMap
        } = this.props;
        let wrapperStyle = {display: 'flex', width:'100vw', marginLeft: 'calc(-50vw + 100%)', justifyContent:'center', top:'-35px'}
        // console.log(this.findIndexOfCurrentStep())
        // const index = Data.map(e => e.name).indexOf('Nick');
        let currentIndex = -1;
        let length = 0;
        return <div style = {wrapperStyle}> {Object.keys(stepMap).map((key, index )=> {
            if(key === checkoutStep){
            currentIndex = index;
        }   
        length ++;
            return  this.renderProgressLineAndPoint(key, index, currentIndex, length)    
        }
            // if(key === this.stepMap[checkoutStep]){}
            // currentIndex = index
            // this.renderProgressPoint(key, index)

        )}
        </div>

    }

    renderProgressLineAndPoint(key, index, currentIndex, length){
        const lineStyle = {display: 'flex'}
        return <div style={lineStyle}>{this.renderProgressLine()}{this.renderProgressPoint(key, index, currentIndex, length)}</div>
    }
    renderProgressLine(){
        const lineStyle = {height: '10px', width: '100px', backgroundColor: 'red', top: '20px'}
        return <div style={lineStyle}></div>
    }
    renderProgressPoint(key, index, currentIndex, length) {
        const circleStyle = {height: '50px', width: '50px', backgroundColor: 'red', borderRadius: '50%'}
        const divStyle = {display: 'flex', flexDirection: 'column', alignItems: 'center', width: '50px'}
        let text = index + 1
        console.log(length)
        console.log(index)
        if(index === length) return null
        if(index < currentIndex || currentIndex === -1){
            text = "+"
        }
        return <div style={divStyle}>
            {/* {this.stepMap} */}

            <div style={circleStyle}>{
            text}</div>
            <div style={{position:'relative'}}>
            {
            this.props.stepMap[key].title
            }
            </div>
            </div>
    }


    render() {
        console.log(this.props)
        return (
            <div>
                {this.renderProgressBar()}
            </div>
            // <div block="CarouselScroll" ref={ this.carouselRef }>
            //     { this.renderArrow() }
            //     { this.renderContent() }
            //     { this.renderArrow(true) }
            // </div>
        );
    }
}

export default StepProgressBar;
